import { createRouter, createWebHistory } from 'vue-router';
import HomePage from '../views/Index.vue';
import All from '../views/allgames.vue';

const routes = [
  {
    path: '/',
    name: 'Home',
    component: HomePage
  },
  {
    path: '/allgames',
    name: 'All',
    component: All
  }
  // 可以在这里添加其他路由
];

const router = createRouter({
  history: createWebHistory(),
  routes
});

export default router;
